const initialState = {
  working: [],
  worked: null,
  notice: null,
};

export default function (state = initialState, action) {
  const { type, payload } = action;
  switch (type) {
    case "add":
      return {
        ...state,
        working: [payload.working, ...state.working],
        notice: payload.mess,
      };
    case "remove":
      return {
        ...state,
      };
    case "error":
      return {
        ...state,
        notice: payload.mess,
      };
    default:
      return state;
  }
}
