import { useState } from "react";
import "./App.css";
import TodoItem from "./task/TodoItem.js";
// import todoItem from "./task/todoItem.js";

function App() {
  const [working, setWorking] = useState("");
  const onSubmit = () => async (dispatch) => {
    // e.preventDefault();
    console.log("active");
    dispatch({
      type: "add",
      payload: working,
    });
  };
  return (
    <div className="container">
      <main>
        <form action="#" className="formTodo">
          <input
            className="todo"
            type="text"
            placeholder="todo"
            id="inputText"
            value={working}
            onChange={(e) => setWorking(e.target.value)}
          />
          {/* <label htmlFor="inputText">Nhap viec can l√†m</label> */}
          <button className="btnSubmit" onClick={(e) => onSubmit(e)}>
            Submit
          </button>
        </form>
        <div className="list">
          <ul>
            <TodoItem task="Cong viec 1" />
          </ul>
        </div>
      </main>
    </div>
  );
}

export default App;
