const initialState = {
  process: [],
  finished: [],
  notice: null,
};

export default function (state = initialState, action) {
  const { type, payload } = action;
  switch (type) {
    case "add":
      return {
        ...state,
        process: [payload.process, ...state.process],
        notice: payload.mess,
      };
    case "remove":
      return {
        ...state,
        finished: [payload.finished, ...state.finished],
      };
    case "error":
      return {
        ...state,
        notice: payload.mess,
      };
    default:
      return state;
  }
}
