import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import store from "../store";
import PropTypes from "prop-types";

function TodoItem({ todoReducer: { process, finished } }) {
  // const [done, setDone] = useState();
  // useEffect(() => {
  //   console.log(done);
  // }, []);
  const doneWork = (task) => {
    store.dispatch({
      type: "remove",
      payload: { finished: task, mess: "finished working" },
    });
  };
  const back = (task) => {
    store.dispatch({
      type: "add__again",
      payload: { process: task, mess: "update success" },
    });
  };

  return (
    <>
      {process?.map((item) => (
        <li>
          <input
            type="checkbox"
            checked={false}
            className="list__checkbox"
            onChange={() => {
              doneWork(item.task);
            }}
          />
          <p>{item.task}</p>
        </li>
      ))}
      {finished?.map((item) => (
        <li>
          <input
            type="checkbox"
            checked={true}
            className="list__checkbox"
            onChange={() => {
              back(item.task);
            }}
          />
          <p>{item.task}</p>
        </li>
      ))}
    </>
  );
}
TodoItem.propTypes = {
  todoReducer: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  todoReducer: state.todoReducer,
});

export default connect(mapStateToProps, null)(TodoItem);
