import { useState } from "react";
import "./App.css";
import TodoItem from "./task/TodoItem.js";
import store from "./store";
// import todoItem from "./task/todoItem.js";

function App() {
  const [working, setWorking] = useState("");
  const onSubmit = (e) => {
    e.preventDefault();
    // console.log("active");
    if (working !== "") {
      store.dispatch({
        type: "add",
        payload: { process: working, mess: "success" },
      });
    } else {
      store.dispatch({
        type: "error",
        payload: { mess: "todo don't create" },
      });
    }
    console.log(working);
    setWorking("");
  };
  return (
    <div className="container">
      <main>
        <form action="#" className="formTodo" onSubmit={(e) => onSubmit(e)}>
          <input
            className="todo"
            type="text"
            placeholder="todo"
            id="inputText"
            value={working}
            onChange={(e) => setWorking(e.target.value)}
          />
          {/* <label htmlFor="inputText">Nhap viec can l√†m</label> */}
          <button className="btnSubmit" onClick={(e) => onSubmit(e)}>
            Submit
          </button>
        </form>
        <div className="list">
          <ul>
            <TodoItem task="Cong viec 1" />
          </ul>
        </div>
      </main>
    </div>
  );
}

export default App;
